def get_fit(X,Y):
    z = np.polyfit(X, Y, 3)

    print("solution:")
    print("{} x^3 + {} x^2 + {} x + {} = y".format(z[0], z[1], z[2], z[3]))

    p = np.poly1d(z)

    y_new = np.zeros(len(X))
    for i in range(len(X)):
        x_1 = X[i]
        y_new[i] = p(x_1)
    return y_new


